import React from 'react';
import {withRouter} from 'react-router-dom';
import axios from 'axios';

const EditButton = props => {
    return (
        <button
            onClick={() => {
                props.history.push(`/users/${props.userid}`);
            }}>
            Edit  
        </button>
    );
};
const EditUserButton = withRouter(EditButton);

function deleteUser(userid) {
    axios
        .delete(`http://localhost:4000/api/users/deleteone/${userid}`)
        .then(response => {
            alert('The user has been deleted!');
            window.location.reload();
        })
        .catch(err => {
            alert(err);
        });
}

const UserRow = props => {
    return (
        <tr
            style={{ cursor: 'pointer' }}
        >
            <td>{props.user.firstname}</td>
            <td>{props.user.lastname}</td>
            <td>{props.user.sex}</td>
            <td>{props.user.age}</td>
            <td>
                <EditUserButton userid={props.user._id} username={props.user.firstname}/>
            </td>
            <td>
                <button onClick={()=> deleteUser(props.user._id)}>Delete</button>
            </td>
        </tr>
    );
};

export default UserRow;
