const createUser = (
    state = {isLoading: false, error: '', data: {} },
    action
) => {
    switch (action.type) {
        case 'CREATE_USER_START':
            return {
                ...state,
                isLoading: true
            };
        case 'CREATE_USER_SUCCESS':
            return {
                ...state,
                isLoading: false,
                data: action.currentData
            };
        case 'CREATE_USER_FAIL':
            return {
                ...state,
                data: {},
                isLoading: false,
                error: action.error
            };
        default:
            return state;
    }
};

export default createUser;
