import React, {Component} from 'react';
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom';

import {getUsers} from '../../actions/users';

import UserRow from '../../components/UserRow';

const CreateButton = props => {
    return (
        <button
            onClick={() => {
                props.history.push('/create');
            }}>
            Create New User  
        </button>
    );
};
const CreateNewUserButton = withRouter(CreateButton);

class Users extends Component {
    componentDidMount() {
        const { dispatch } = this.props;
        dispatch(getUsers());
    }

    render() {
        const { users } = this.props;
        let usersUI;
        if(users.isLoading) {
            usersUI = <p>Loading</p>;
        } else if (users.error !== '') {
            usersUI = <p style={{color: 'red'}}>{users.error}</p>
        } else if (users.data.length !== 0) {
            usersUI = (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th style={{cursor: 'pointer'}} onClick={()=>{users.sort((a,b) => a.firstname - b.firstname)}}>First Name</th>
                                <th style={{cursor: 'pointer'}}>Last Name</th>
                                <th>Sex</th>
                                <th>Age</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.data.map(user => {
                                return (
                                    <UserRow
                                        key = {user._id}
                                        user = {user}
                                    />
                                );
                            })}
                        </tbody>
                    </table>
                    <CreateNewUserButton />
                </div>
            );
        }
        return (
            <div>
                <h3>User List</h3>
                {usersUI}
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        users: state.users
    };
};

export default connect(mapStateToProps)(Users);
