import React, {Component} from 'react';
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom';

import {getUsers} from '../../actions/users';

import UserRow from '../../components/UserRow';
import EnhancedTableHead from '../../components/EnhancedTableHead';
import EnhancedTableToolbar from '../../components/EnhancedTableToolbar';

import PropTypes from 'prop-types';
import { withStyle } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TablePagination from '@material-ui/core/TablePagination';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Tooltip from '@material-ui/core/Tooltip';
import { lighten } from '@material-ui/core/styles/colorManipulator';

//Create User Button
const CreateButton = props => {
    return (
        <button
            onClick={() => {
                props.history.push('/create');
            }}>
            Create New User  
        </button>
    );
};
const CreateNewUserButton = withRouter(CreateButton);

//Table
let counter = 0;
function createData(firstname, lastname, sex, age) {
    counter += 1;
    return { id: counter, firstname, lastname, sex, age };
}

function desc(a, b, orderBy) {
    if (b[orderBy] < a[orderBy]) {
        return -1;
      }
      if (b[orderBy] > a[orderBy]) {
        return 1;
      }
      return 0;
}

function stableSort(array, cmp) {
    const stabilizedThis = array.map((el, index) => [el, index]);
    stabilizedThis.sort((a, b) => {
        const order = cmp(a[0], b[0]);
        if (order !== 0) return order;
        return a[1] - b[1];
    });
    return stabilizedThis.map(el => el[0]);
}

function getSorting(order, orderBy) {
    return order === "desc"
    ? (a, b) => desc(a, b, orderBy)
    : (a, b) => -desc(a, b, orderBy);
}

const styles = theme => ({
    root: {
        width: "100%",
        marginTop: theme.spacing.unit * 3
      },
      table: {
        minWidth: 1020
      },
      tableWrapper: {
        overflowX: "auto"
      }
});

class Users extends Component {
    state = {
        order: 'asc',
        orderBy: 'firstname',
        page: 0,
        rowsPerPage: 5
    };

    componentDidMount() {
        const { dispatch } = this.props;
        dispatch(getUsers());
    }

    handleRequestSort = (event, property) => {
        const orderBy = property;
        let order = "desc";
        if (this.state.orderBy === property && this.state.order === "desc") {
            order = "asc";
        }
        this.setState({ order, orderBy });
    };

    handleChangePage = (event, page) => {
        this.setState({ page });
    };

    handleChangeRowsPerPage = event => {
        this.setState({ rowsPerPage: event.target.value });
    };


    render() {
        const { users, classes } = this.props;
        const { order, orderBy, rowsPerPage, page } = this.state;
        const emptyRows = rowsPerPage - Math.min(rowsPerPage, data.length - page * rowsPerPage);
        let usersUI;

        if(users.isLoading) {
            usersUI = <p>Loading</p>;
        } else if (users.error !== '') {
            usersUI = <p style={{color: 'red'}}>{users.error}</p>
        } else if (users.data.length !== 0) {
            usersUI = (
                <Paper className = {classes.root}>
                    <table>
                        <thead>
                            <tr>
                                <th style={{cursor: 'pointer'}}>First Name</th>
                                <th style={{cursor: 'pointer'}}>Last Name</th>
                                <th>Sex</th>
                                <th>Age</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.data.map(user => {
                                return (
                                    <UserRow
                                        key = {user._id}
                                        user = {user}
                                    />
                                );
                            })}
                        </tbody>
                    </table>
                    <CreateNewUserButton />
                </Paper>
            );
        }
        return (
            <div>
                <h3>User List</h3>
                {usersUI}
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        users: state.users
    };
};

export default connect(mapStateToProps)(Users);
