import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import axios from 'axios';

import Paper from '@material-ui/core/Paper';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';


const ReturnButton = props => {
    return (
        <button
            onClick={() => {
                props.history.push('/users');
            }}>
            Return to User List    
        </button>
    );
};
const WithRouterButton = withRouter(ReturnButton);

class CreateNewUser extends Component {
    constructor(props) {
        super(props);
        this.state = {firstname: '', lastname: '', sex: '', age: '', psWord: '', repeat: ''};
    }

    handleFirstNameChange = e => {
        this.setState({firstname: e.target.value});
    }

    handleLastNameChange = e => {
        this.setState({lastname: e.target.value});
    }

    handleSexChange = e => {
        this.setState({sex: e.target.value});
    }

    handleAgeChange = e => {
        this.setState({age: e.target.value});
    }

    handlePassWordChange = e => {
        this.setState({psWord: e.target.value});
    }

    handleRepeatChange = e => {
        this.setState({repeat: e.target.value});
    }

    handleSubmit = e => {
        e.preventDefault();
        if(this.state.psWord === this.state.repeat) {
            axios
                .post(`http://localhost:4000/api/users/insertone`, this.state)
                .then(response => {
                    alert("The user has been created!");
                })
                .catch(err => {
                    alert(err);
                });
        } else {
            alert("Password didn't match!");
        }
    };

    render() {
        return (
            <Paper className='root'>

                <Toolbar>
                    <Typography variant='h4'>
                        Create New User
                    </Typography>

                </Toolbar>

                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>
                            First Name
                            <input
                                type='text'
                                value={this.state.firstname}
                                onChange={this.handleFirstNameChange}
                                placeholder="First Name"
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Last Name
                            <input
                                type='text'
                                value={this.state.lastname}
                                onChange={this.handleLastNameChange}
                                placeholder="Last Name"
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Sex
                            <input
                                type='text'
                                value={this.state.sex}
                                onChange={this.handleSexChange}
                                placeholder="Sex"
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Age
                            <input
                                type='text'
                                value={this.state.age}
                                onChange={this.handleAgeChange}
                                placeholder="Age"
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Password
                            <input
                                type='password'
                                value={this.state.psWord}
                                onChange={this.handlePassWordChange}
                                placeholder="Password"
                            />
                        </label>
                    </div>
                    <div>
                        <label>
                            Repeat Password
                            <input
                                type='password'
                                value={this.state.repeat}
                                onChange={this.handleRepeatChange}
                                placeholder="Repeat Password"
                            />
                        </label>
                    </div>
                    <div>
                        <input type='submit' value='submit' />
                    </div>
                </form>
                <WithRouterButton />
            </Paper>
        )
    }
}

export default CreateNewUser;
